#-------------------------------------------------------------------------------
# 32 Point Groups
#-------------------------------------------------------------------------------
struct PointGroup <: AbstractFiniteGroup
    name::String
    multab::Matrix{Int64}
    inv::Vector{Int64}
    cls::Vector{Vector{Int64}}
    clsv::Vector{Int64}
    mult::Vector{Int64}
    operations::Vector{String}
end

function Base.display(g::PointGroup)
    println("Point group : $(name(g))")
    println("Group order : $(order(g))")
    println("Classes     : $(length(class(g)))")
end

export pointgroup
function pointgroup(i::Integer)
    multab = PointGroupMultiplicationTables[i]
    ginv = PointGroupInverses[i]
    cls = PointGroupClasses[i]
    clsv = PointGroupClassVectors[i]
    mult = length.(cls)
    PointGroup(
        PointGroups[i], 
        multab,
        ginv,
        cls,
        clsv,
        mult,
        PointGroupOperationNames[i]
    )
end
pointgroup(s::String) = pointgroup(PointGroupDict[s])

export groupindex, operation, matrix, rotation, repname
name(g::PointGroup, i::Integer) = operation(g, i)
groupindex(g::PointGroup) = PointGroupDict[name(g)]
operation(g::PointGroup) = g.operations
operation(g::PointGroup, i) = g.operations[i]
matrix(g::PointGroup) = PointGroupRotationMatrices[groupindex(g)]
matrix(g::PointGroup, i) = PointGroupRotationMatrices[groupindex(g)][i]
function rotation(g::PointGroup)
    GI = groupindex(g)
    if HexagonalGroups[GI]
        invhex = inv(HexagonalAxes)
        [HexagonalAxes * m * invhex for m in PointGroupRotationMatrices[GI]]
    else
        PointGroupRotationMatrices[GI]
    end
end
function rotation(g::PointGroup, i::Integer)
    GI = groupindex(g)
    if HexagonalGroups[GI]
        mat = PointGroupRotationMatrices[GI][i]
        HexagonalAxes * mat * inv(HexagonalAxes)
    else
        PointGroupRotationMatrices[GI][i]
    end
end
function rotation(g::PointGroup, i::AbstractVector{<:Integer})
    GI = groupindex(g)
    if HexagonalGroups[GI]
        invhex = inv(HexagonalAxes)
        [HexagonalAxes * m * invhex for m in PointGroupRotationMatrices[GI][i]]
    else
        PointGroupRotationMatrices[GI][i]
    end
end

function repname(g::PointGroup, i::Integer; convention::Integer=1)
    if convention == 1
        PointGroupRepresentationNames[groupindex(g)][i]
    elseif convention == 2
        PointGroupRepresentationNames2[groupindex(g)][i]
    end
end

function repname(g::PointGroup, χ::AbstractVector; convention::Integer=1)
    ind = pg_irrep_index(g, χ)
    repname(g, ind, convention=convention)
end

function pg_irrep_index(g::PointGroup, χ::AbstractVector)
    ct = charactertable(g)
    k = 0
    for i = 1:size(ct, 1)
        if norm(ct[i, :] .- χ) < 1e-7 
            k = i 
            break
        end
    end
    iszero(k) && error("No such character χ = $χ.")
    k
end

function charactertable(g::PointGroup)
    GI = groupindex(g)
    tabmat = PointGroupCharacterTables[GI]
    tab = [Characters(g, tabmat[i,:]) for i = 1:size(tabmat, 1)]
    names = PointGroupRepresentationNames[GI]
    CharacterTable(tab, names)
end

function irreps(g::PointGroup; R::Bool=false)
    GI = groupindex(g)
    reps = PointGroupRepresentations[GI]
    if R
        map(reps) do rep 
            if sum(norm.(imag.(rep))) > 1e-7
                [[real(m) imag(m); -imag(m) real(m)] for m in rep]
            else
                rep 
            end
        end
    else
        reps
    end
end

function irreps(g::PointGroup, χ::AbstractVector; R::Bool=false)
    GI = groupindex(g)
    repindex = pg_irrep_index(g, χ)
    rep = PointGroupRepresentations[GI][repindex]
    if R && sum(norm.(imag.(rep))) > 1e-7
        [[real(m) imag(m); -imag(m) real(m)] for m in rep]
    else
        rep
    end
end

function irreps(g::PointGroup, χ::AbstractMatrix; R::Bool=false)
    [irreps(g, χ[i,:], R=R) for i=1:size(χ,1)]
end
